mapper.select:
select no, name, content, date_format(reg_date, '%y-%m-%d %h:%m:%s') as 'reg_date' from guestbook
where no < #{ _parameter }
order by no desc limit 0,5


select  no, name, content, date_format(reg_date, '%y-%m-%d %h:%m:%s') as 'reg_date'
  from guestbook
where no=#{no}

mapper.delete:

	delete from guestbook 
	where no=#{no} 
	  and password=password(#{password})
