mapper.select:
select no, category_no as categoryNo, blog_no as blogNo, title, content, date_format(reg_date, '%Y-%m-%d') as regDate from post where blog_no=#{blogNo}
and category_no is null
and category_no=#{categoryNo}
order by reg_date desc;


	select no, category_no, blog_no, title, content, date_format(reg_date, '%Y-%m-%d') as reg_date
	from post
	where no=#{no}
	order by reg_date desc;
